// src/App.js
import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';
import LoginPage from './components/LoginPage';
import axios from 'axios';

const App = () => {
    const [appointments, setAppointments] = useState([]);
    const [userType, setUserType] = useState(null); // 'doctor' or 'patient'

    // Fetch appointments on initial load
    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://127.0.0.1:5000/appointments');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointments(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, []);

    const handleLogin = (type) => {
        setUserType(type);
    };

    const handleCancelAppointment = async (id) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${id}/cancel`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Failed to cancel appointment');
            }

            const data = await response.json();

            if (data.success) {
                setAppointments(
                    appointments.map((appointment) =>
                        appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
                    )
                );
            } else {
                console.error('Error cancelling appointment:', data.message);
            }
        } catch (error) {
            console.error('Error cancelling appointment:', error);
        }
    };

    const handleSubmitNote = async (id, newNote) => {
        try {
            await axios.post(`http://127.0.0.1:5000/appointments/${id}/add-note`, { note: newNote.text });
            setAppointments((prevAppointments) =>
                prevAppointments.map((appointment) =>
                    appointment.id === id
                        ? { ...appointment, notes: [...appointment.notes, newNote] }
                        : appointment
                )
            );
            return true;
        } catch (error) {
            console.error('Error submitting note:', error);
            return false;
        }
    };

    const handleBookAppointment = (newAppointment) => {
        const username = localStorage.getItem('username'); // Get the username from local storage

        if (!username) {
            alert('Please log in to book an appointment');
            return;
        }

        const appointmentData = {
            doctor_name: newAppointment.doctor_name,
            patient_name: username, // Use the logged-in username as the patient name
            date: newAppointment.date,
            status: 'Scheduled',
            notes: newAppointment.notes,
        };

        axios
            .post('http://127.0.0.1:5000/appointments', appointmentData)
            .then((response) => {
                setAppointments((prevAppointments) => [
                    ...prevAppointments,
                    {
                        id: response.data.id,
                        doctor_name: appointmentData.doctor_name,
                        patient_name: username,
                        date: appointmentData.date,
                        status: appointmentData.status,
                        notes: appointmentData.notes,
                    },
                ]);
            })
            .catch((error) => {
                console.error('Error booking appointment:', error);
            });
    };

    if (!userType) {
        return <LoginPage onLogin={handleLogin} />;
    }

    return (
        <div className="App">
            <h1>Hospital Management System</h1>

            {userType === 'doctor' ? (
                <DoctorDashboard
                    appointments={appointments}
                    onCancel={handleCancelAppointment}
                    onSubmitNote={handleSubmitNote}
                />
            ) : (
                <PatientDashboard
                    appointments={appointments.filter((appointment) => appointment.patient_name === localStorage.getItem('username'))}
                    onCancel={handleCancelAppointment}
                    onSubmitNote={handleSubmitNote}
                    onBookAppointment={handleBookAppointment}
                />
            )}
        </div>
    );
};

export default App;
