import React, { useState } from 'react';
import AppointmentCard from './AppointmentCard';

const PatientDashboard = ({ initialAppointments = [], onCancel, onSubmitNote }) => {
    const [appointments, setAppointments] = useState(initialAppointments);
    const [newAppointment, setNewAppointment] = useState({ date: '', note: '' });

    // Handle input changes for new appointment form
    const handleInputChange = (e) => {
        setNewAppointment({ ...newAppointment, [e.target.name]: e.target.value });
    };

    // Handle booking a new appointment
    const handleBookAppointment = async () => {
        try {
            const appointmentData = {
                doctor_name: "Dr. Jane Smith",  // Hardcoded for now
                patient_name: "Patient Name",  // Replace with the actual patient name dynamically
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [newAppointment.note],  // Send the note as an array
            };

            const response = await fetch('http://127.0.0.1:5000/appointments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(appointmentData),
            });

            if (!response.ok) {
                throw new Error('Failed to create appointment');
            }

            const createdAppointment = await response.json();

            // Update the appointments state with the new appointment
            setAppointments([...appointments, createdAppointment]);

            // Clear the new appointment form
            setNewAppointment({ date: '', note: '' });

        } catch (error) {
            console.error('Error booking appointment:', error);
        }
    };

    // Handle note submission
    const handleAddNote = async (appointmentId, newNote) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${appointmentId}/add-note`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ note: newNote }),
            });

            if (!response.ok) {
                throw new Error('Failed to add note');
            }

            setAppointments(appointments.map(app => 
                app.id === appointmentId ? { ...app, notes: [...app.notes, newNote] } : app
            ));

        } catch (error) {
            console.error('Error submitting note:', error);
        }
    };

    // Handle appointment cancellation
    const handleCancelAppointment = async (appointmentId) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${appointmentId}/cancel`, {
                method: 'POST',
            });

            if (!response.ok) {
                throw new Error('Failed to cancel appointment');
            }

            setAppointments(appointments.map(app => 
                app.id === appointmentId ? { ...app, status: 'Cancelled' } : app
            ));

        } catch (error) {
            console.error('Error cancelling appointment:', error);
        }
    };

    return (
        <div className="dashboard">
            <h2>Patient's Appointments</h2>

            {/* Check if appointments is an array and has data */}
            {appointments && appointments.length > 0 ? (
                appointments.map((appointment) => (
                    <AppointmentCard
                        key={appointment.id}
                        appointment={appointment}
                        onCancel={handleCancelAppointment}
                        onSubmitNote={handleAddNote}
                    />
                ))
            ) : (
                <p>No appointments available.</p>
            )}

            <div className="appointment-booking">
                <h3>Book a new appointment</h3>
                <input
                    type="date"
                    name="date"
                    value={newAppointment.date}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="note"
                    placeholder="Add a note"
                    value={newAppointment.note}
                    onChange={handleInputChange}
                />
                <button onClick={handleBookAppointment}>Book Appointment</button>
            </div>
        </div>
    );
};

export default PatientDashboard;
