import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';

const App = () => {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://127.0.0.1:5000/appointments');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                // Update appointments with fetched data
                setAppointments(data);
                console.log(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, []);

    const handleCancelAppointment = (id) => {
        setAppointments(
            appointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
            )
        );
    };

    const handleSubmitNote = async (id, newNote) => {
        console.log(id, newNote);

        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${id}/add-note`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ note: newNote }),
            });

            if (!response.ok) {
                throw new Error('Failed to submit note');
            }

            // Update local state with the new note
            setAppointments(
                appointments.map((appointment) =>
                    appointment.id === id
                        ? { ...appointment, notes: [...appointment.notes, newNote] }
                        : appointment
                )
            );
        } catch (error) {
            console.error('Error submitting note:', error);
        }
    };


    const handleBookAppointment = (newAppointment) => {
        const newId = appointments.length + 1;
        setAppointments([
            ...appointments,
            {
                id: newId,
                doctor_name: 'Dr. Jane Smith', // Adjust to match your data structure
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [{ author: 'Patient', text: newAppointment.note }],
                isDoctor: false,
            },
        ]);
    };

    return (

        <div className="App">
            <h1>Hospital Management System</h1>

            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />

            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
