import React, { useState } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => 
{
    const [note, setNote] = useState(''); // Input for new note
    const [notes, setNotes] = useState(appointment.notes || []); // List of existing notes

    const handleNoteChange = (e) => 
    {
        setNote(e.target.value);
    };

    const handleNoteSubmit = () => 
    {
        const newNote = {
            text: note,
            author: appointment.isDoctor ? 'Doctor' : 'Patient', // Checking who adds the note
        };
        setNotes([...notes, newNote]); // Add new note to the list
        setNote(''); // Clear input after submission
        onSubmitNote(appointment.id, newNote); // Callback to save note (you can later link this to the backend)
    };

    return (
        <div className="appointment-card">
            <h3>Appointment with: {appointment.doctorName}</h3>
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>

            {/* Display existing notes */}
            <div className="notes-section">
                {notes.length > 0 && (
                    <div>
                        <h4>Notes:</h4>
                        {notes.map((note, index) => (
                            <p key={index}>
                                <strong>{note.author} Note:</strong> {note.text}
                            </p>
                        ))}
                    </div>
                )}
            </div>

            {/* Input for adding a new note */}
            <input
                type="text"
                placeholder="Add a note"
                value={note}
                onChange={handleNoteChange}
            />
            <div className="appointment-actions">
                <button onClick={handleNoteSubmit}>Submit Note</button>
                <button onClick={() => onCancel(appointment.id)}>Cancel Appointment</button>
            </div>
        </div>
    );
};

export default AppointmentCard;
