import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';

const App = () => {
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch appointments from the API
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/appointments'); // Change this URL to your API endpoint
                const data = await response.json();
                setAppointments(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchAppointments();
    }, []);

    const handleCancelAppointment = (id) => {
        setAppointments(
            appointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
            )
        );
    };

    const handleSubmitNote = (id, newNote) => {
        setAppointments(
            appointments.map((appointment) =>
                appointment.id === id
                    ? { ...appointment, notes: [...appointment.notes, newNote] }
                    : appointment
            )
        );
    };

    const handleBookAppointment = (newAppointment) => {
        const newId = appointments.length + 1; // Replace with your logic to generate unique IDs
        setAppointments([
            ...appointments,
            {
                id: newId,
                doctorName: 'Dr. Jane Smith', // Replace with logic to get doctor name
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [{ author: 'Patient', text: newAppointment.note }],
                isDoctor: false,
            },
        ]);
    };

    if (loading) {
        return <div>Loading...</div>; // Loading state
    }

    return (
        <div className="App">
            <h1>Hospital Management System</h1>

            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />

            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
