import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';
import LoginPage from './components/LoginPage';
import axios from 'axios';
import './styles/App.css'; // Ensure you import your CSS file

const App = () => {
    const [appointments, setAppointments] = useState([]);
    const [userType, setUserType] = useState(null);

    useEffect(() => {
        // Check if user is logged in
        const storedUserType = localStorage.getItem('userType');
        if (storedUserType) {
            setUserType(storedUserType);
        }

        // Fetch appointments on initial load if user is logged in
        const fetchAppointments = async () => {
            const token = localStorage.getItem('token'); // Get the JWT token from local storage
            if (!token) return; // If there's no token, don't fetch appointments

            try {
                const response = await fetch('http://127.0.0.1:5000/appointments', {
                    headers: {
                        'Authorization': `Bearer ${token}`, // Add the token to the request headers
                    }
                });
                if (!response.ok) {
                    const errorBody = await response.text();
                    console.error('Error fetching appointments:', response.status, errorBody);
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointments(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, []);

    const handleLogin = (type) => {
        setUserType(type);
    };

    const handleLogout = () => {
        localStorage.removeItem('username');
        localStorage.removeItem('userType');
        localStorage.removeItem('token'); // Remove the token on logout
        setUserType(null); // Reset user type
        setAppointments([]); // Clear appointments on logout
    };

    const handleCancelAppointment = async (id) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${id}/cancel`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`, // Add the token to the request headers
                },
            });

            if (!response.ok) {
                const errorBody = await response.text();
                console.error('Error cancelling appointment:', response.status, errorBody);
                throw new Error('Failed to cancel appointment');
            }

            const data = await response.json();

            if (data.success) {
                // Update appointments to set the status of the cancelled appointment
                setAppointments(
                    appointments.map((appointment) =>
                        appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
                    )
                );
            } else {
                console.error('Error cancelling appointment:', data.message);
            }
        } catch (error) {
            console.error('Error cancelling appointment:', error);
        }
    };

    const handleSubmitNote = async (id, newNote) => {
        const author = userType === 'doctor' ? 'Doctor' : 'Patient'; // Determine the author based on user type
        try {
            // Send both the note text and the author to the backend
            await axios.post(`http://127.0.0.1:5000/appointments/${id}/add-note`, {
                note: newNote.text,
                author: author // Include the author
            }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}` // Add the token to the request headers
                }
            });

            // Update the appointments state with the new note
            setAppointments((prevAppointments) =>
                prevAppointments.map((appointment) =>
                    appointment.id === id
                        ? {
                            ...appointment,
                            notes: [...(appointment.notes || []), { author: author, text: newNote.text }] // Ensure notes are initialized
                        }
                        : appointment
                )
            );
            return true;
        } catch (error) {
            console.error('Error submitting note:', error);
            return false;
        }
    };

    const handleBookAppointment = async (newAppointment) => {
        try {
            const response = await axios.post('http://127.0.0.1:5000/appointments', {
                doctor_name: 'Dr. Jane Smith', // Replace with actual doctor name if needed
                patient_name: localStorage.getItem('username'), // Use stored username
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [newAppointment.note] // You might want to adjust this based on your notes structure
            }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}` // Add the token to the request headers
                }
            });

            if (response.status === 201) {
                // Add the new appointment to state
                setAppointments((prevAppointments) => [
                    ...prevAppointments,
                    {
                        id: response.data.id, // Use the id returned by the backend
                        doctor_name: response.data.doctor_name,
                        patient_name: response.data.patient_name,
                        date: newAppointment.date,
                        status: 'Scheduled',
                        notes: [], // Initialize with empty notes or handle appropriately
                    },
                ]);
            }
        } catch (error) {
            console.error('Error booking appointment:', error);
        }
    };

    // Show the login page if the user has not selected a user type
    if (!userType) {
        return <LoginPage onLogin={handleLogin} />;
    }

    // Filter appointments for the logged-in patient
    const filteredAppointments =
        userType === 'patient'
            ? appointments.filter((appointment) => appointment.patient_name === localStorage.getItem('username'))
            : appointments;

    return (
        <div className="App">
            <h1>Hospital Management System</h1>
            <button onClick={handleLogout}>Logout</button>

            {userType === 'doctor' ? (
                <DoctorDashboard
                    appointments={filteredAppointments} // All appointments passed to DoctorDashboard
                    onCancel={handleCancelAppointment}
                    onSubmitNote={handleSubmitNote}
                />
            ) : (
                <PatientDashboard
                    appointments={filteredAppointments} // Filtered appointments for the patient
                    onCancel={handleCancelAppointment}
                    onSubmitNote={handleSubmitNote}
                    onBookAppointment={handleBookAppointment}
                />
            )}
        </div>
    );
};

export default App;
