// src/App.js

import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';
import Login from './components/Login';

const App = () => {
    const [appointments, setAppointments] = useState([]);
    const [userType, setUserType] = useState(null);
    const [username, setUsername] = useState('');

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://127.0.0.1:5000/appointments');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointments(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, []);

    const handleCancelAppointment = async (id) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/appointments/${id}/cancel`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Failed to cancel appointment');
            }

            const data = await response.json();

            if (data.success) {
                setAppointments(
                    appointments.map((appointment) =>
                        appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
                    )
                );
            } else {
                console.error('Error cancelling appointment:', data.message);
            }
        } catch (error) {
            console.error('Error cancelling appointment:', error);
        }
    };

    const handleSubmitNote = async (id, newNote) => {
        console.log(id, newNote);
        // Logic for submitting note
    };

    const handleBookAppointment = (newAppointment) => {
        const newId = appointments.length + 1;
        setAppointments([
            ...appointments,
            {
                id: newId,
                doctor_name: 'Dr. Jane Smith',
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [{ author: 'Patient', text: newAppointment.note }],
                isDoctor: false,
            },
        ]);
    };

    const handleLogin = (type, name) => {
        setUserType(type);
        setUsername(name);
    };

    return (
        <div className="App">
            <h1>Hospital Management System</h1>
            {!userType ? (
                <Login onLogin={handleLogin} />
            ) : (
                <>
                    {userType === 'doctor' ? (
                        <DoctorDashboard
                            appointments={appointments.filter(appointment => appointment.isDoctor)}
                            onCancel={handleCancelAppointment}
                            onSubmitNote={handleSubmitNote}
                        />
                    ) : (
                        <PatientDashboard
                            appointments={appointments.filter(appointment => !appointment.isDoctor)}
                            onCancel={handleCancelAppointment}
                            onSubmitNote={handleSubmitNote}
                            onBook={handleBookAppointment}
                        />
                    )}
                </>
            )}
        </div>
    );
};

export default App;
