import React, { useState } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');

    const handleNoteSubmit = async () => {
        const newNote = { author: 'Doctor', text: note }; // Assuming the doctor is adding a note
        const success = await onSubmitNote(appointment.id, newNote);
        if (success) {
            setNote(''); // Clear the input after successful submission
        }
    };

    return (
        <div className="appointment-card">
            <h3>Appointment Details</h3>
            {/* Displaying both doctor and patient names */}
            <p><strong>Doctor:</strong> {appointment.doctor_name}</p>
            <p><strong>Patient:</strong> {appointment.patient_name}</p>
            <p><strong>Date:</strong> {appointment.date}</p>
            <p><strong>Status:</strong> {appointment.status}</p>

            {/* Displaying Notes */}
            <div className="notes-section">
                <h4>Notes:</h4>
                {appointment.notes.length > 0 ? (
                    appointment.notes.map((note, index) => (
                        <p key={index}>
                            <strong>{note.author}: </strong> {note.text}
                        </p>
                    ))
                ) : (
                    <p>No notes available for this appointment.</p>
                )}
            </div>

            {/* Add new note */}
            <div>
                <input
                    type="text"
                    placeholder="Add a note"
                    value={note}
                    onChange={(e) => setNote(e.target.value)}
                />
                <button onClick={handleNoteSubmit}>Submit Note</button>
            </div>

            {/* Cancel Appointment */}
            <button onClick={() => onCancel(appointment.id)}>Cancel Appointment</button>
        </div>
    );
};

export default AppointmentCard;
