import React, { useState, useEffect } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');
    const [notes, setNotes] = useState([]);

    useEffect(() => {
        if (appointment.notes) {
            setNotes(appointment.notes.map(note => ({ text: note.text, author: note.author })));
        }
    }, [appointment.notes]);

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const handleNoteSubmit = async () => {
        if (note.trim() === "") return; // Prevent empty note submissions

        const newNote = {
            text: note,
            author: appointment.isDoctor ? 'Doctor' : 'Patient', // Set the author based on role
        };

        const success = await onSubmitNote(appointment.id, newNote); // Pass the whole newNote object

        if (success) {
            setNotes([...notes, newNote]); // Update notes state locally
            setNote(''); // Clear the note input
        } else {
            console.error('Error submitting note');
        }
    };

    const handleCancelAppointment = async () => {
        await onCancel(appointment.id);
    };

    return (
        <div className="appointment-card">
            <h3>Appointment with: {appointment.doctor_name}</h3>
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>

            <div className="notes-section">
                {notes.length > 0 ? (
                    <div>
                        <h4>Notes:</h4>
                        {notes.map((note, index) => (
                            <p key={index}>
                                <strong>{note.author}:</strong> {note.text}
                            </p>
                        ))}
                    </div>
                ) : (
                    <p>No notes available</p>
                )}
            </div>

            <input
                type="text"
                placeholder="Add a note"
                value={note}
                onChange={handleNoteChange}
            />
            <div className="appointment-actions">
                <button className="submit-note" onClick={handleNoteSubmit}>Submit Note</button>
                <button className="cancel-appointment" onClick={handleCancelAppointment}>Cancel Appointment</button>
            </div>
        </div>
    );
};

export default AppointmentCard;
