// components/Login.js
import React, { useState } from 'react';

const Login = ({ userType, onAuthenticate }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();

        // Replace this with your actual authentication logic
        if (username === 'doctor' && password === 'doctor' && userType === 'doctor') {
            onAuthenticate(true, 'doctor');
        } else if (username === 'patient' && password === 'patient' && userType === 'patient') {
            onAuthenticate(true, 'patient');
        } else {
            setError('Invalid credentials');
        }
    };

    return (
        <div>
            <h2>{userType === 'doctor' ? 'Doctor' : 'Patient'} Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Login;
