import React, { useState } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const handleNoteSubmit = () => {
        if (note.trim()) {
            console.log('Submitting note:', note); // Log the note being submitted
            onSubmitNote(appointment.id, { author: 'Doctor', text: note });
            setNote('');
        }
    };

    return (
        <div className="appointment-card">
            <h3>Appointment with {appointment.doctor_name}</h3>
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>
            <div>
                <h4>Notes:</h4>
                {appointment.notes.map((n, index) => (
                    <p key={index}>{n.author}: {n.text}</p>
                ))}
            </div>
            <input
                type="text"
                value={note}
                onChange={handleNoteChange}
                placeholder="Add a note"
            />
            <button onClick={handleNoteSubmit}>Create Note</button>
            <button onClick={() => onCancel(appointment.id)}>Cancel Appointment</button>
        </div>
    );
};

export default AppointmentCard;
