import React, { useEffect, useState } from 'react';
import AppointmentCard from './AppointmentCard';

const DoctorDashboard = () => {
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate fetching data
        setTimeout(() => {
            const fetchedAppointments = [
                { id: 1, patientName: 'John Doe', date: '2024-10-15', status: 'Scheduled' },
                { id: 2, patientName: 'Jane Smith', date: '2024-10-16', status: 'Scheduled' },
                { id: 3, patientName: 'Mike Johnson', date: '2024-10-17', status: 'Scheduled' },
            ];
            setAppointments(fetchedAppointments);
            setLoading(false);
        }, 1000); // Simulate network delay
    }, []);

    const cancelAppointment = (id) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Canceled' } : appointment
            )
        );
    };

    if (loading) {
        return <div>Loading appointments...</div>;
    }

    return (
        <div className="doctor-dashboard">
            <h1>Doctor's Dashboard</h1>
            <div className="appointments">
                {appointments.map((appointment) => (
                    <AppointmentCard
                        key={appointment.id}
                        appointment={appointment}
                        onCancel={() => cancelAppointment(appointment.id)}
                    />
                ))}
            </div>
        </div>
    );
};

export default DoctorDashboard;
