import React, { useEffect, useState } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';

const App = () => {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        const fetchAppointments = async () => {
            const response = await fetch('http://127.0.0.1:5000/appointments');
            const data = await response.json();
            setAppointments(data);
        };

        fetchAppointments();
    }, []);

    const handleCancelAppointment = async (id) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
            )
        );
    };

    const handleSubmitNote = (id, newNote) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id
                    ? { ...appointment, notes: [...appointment.notes, newNote] }
                    : appointment
            )
        );
    };

    const handleBookAppointment = async (newAppointment) => {
        const response = await fetch('http://127.0.0.1:5000/appointments', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                doctor_name: newAppointment.doctorName || 'Dr. Unknown',
                patient_name: newAppointment.patientName || 'Patient Unknown',
                date: newAppointment.date,
            }),
        });

        if (response.ok) {
            const appointment = await response.json();
            setAppointments((prevAppointments) => [...prevAppointments, appointment]);
        }
    };

    return (
        <div className="App">
            <h1>Hospital Management System</h1>

            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />

            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
