import React, { useState } from 'react';

const AppointmentCard = ({ appointment }) => {
    const [note, setNote] = useState(appointment.note || '');

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const submitNote = () => {
        // Here you can add the logic to submit the note to the backend
        console.log(`Submitted note for appointment ${appointment.id}: ${note}`);
    };

    return (
        <div className="appointment-card">
            <h3>Appointment with {appointment.patientName}</h3>
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>
            <textarea
                value={note}
                onChange={handleNoteChange}
                placeholder="Add your note here"
            />
            <button onClick={submitNote}>Submit Note</button>
        </div>
    );
};

export default AppointmentCard;
