import React, { useState, useEffect } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');
    const [notes, setNotes] = useState([]);

    // Set the initial notes from the appointment prop when it changes
    useEffect(() => {
        setNotes(appointment.notes.map(text => ({ text, author: 'Patient' }))); // Adjust author as necessary
    }, [appointment.notes]);

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const handleNoteSubmit = () => {
        if (note.trim() === "") return;

        const newNote = {
            text: note,
            author: appointment.isDoctor ? 'Doctor' : 'Patient',
        };

        // Update notes state
        const updatedNotes = [...notes, newNote];
        setNotes(updatedNotes);
        setNote('');
        onSubmitNote(appointment.id, newNote);
    };

    return (
        <div className="appointment-card">
            <h3>Appointment with: {appointment.doctor_name}</h3> {/* Accessing doctor_name from backend data */}
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>

            <div className="notes-section">
                {notes.length > 0 && (
                    <div>
                        <h4>Notes:</h4>
                        {notes.map((note, index) => (
                            <p key={index}>
                                <strong>{note.author} Note:</strong> {note.text}
                            </p>
                        ))}
                    </div>
                )}
            </div>

            <input
                type="text"
                placeholder="Add a note"
                value={note}
                onChange={handleNoteChange}
            />
            <div className="appointment-actions">
                <button className="submit-note" onClick={handleNoteSubmit}>Submit Note</button>
                <button className="cancel-appointment" onClick={() => onCancel(appointment.id)}>Cancel Appointment</button>
            </div>
        </div>
    );
};

export default AppointmentCard;
