import React, { useState, useEffect } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';
import axios from 'axios';

const App = () => {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://127.0.0.1:5000/appointments');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAppointments(data);
                console.log(data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, []);

    const handleCancelAppointment = async (id) => {
        try {
            // Make a DELETE request to cancel the appointment
            await axios.delete(`http://127.0.0.1:5000/appointments/${id}/cancel`);
            setAppointments((prevAppointments) =>
                prevAppointments.map((appointment) =>
                    appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
                )
            );
        } catch (error) {
            console.error('Error cancelling appointment:', error);
        }
    };

    const handleSubmitNote = async (id, newNote) => {
        try {
            const response = await axios.post(`http://127.0.0.1:5000/appointments/${id}/add-note`, {
                note: newNote.text, // Use the text from the note object
            });

            if (response.status === 200) {
                // Update the local state to reflect the new note
                setAppointments((prevAppointments) =>
                    prevAppointments.map((appointment) =>
                        appointment.id === id
                            ? { ...appointment, notes: [...appointment.notes, newNote] }
                            : appointment
                    )
                );
                console.log("Note added successfully");
                return true; // Indicate success
            }
        } catch (error) {
            console.error('Error adding note:', error);
        }
        return false; // Indicate failure
    };

    const handleBookAppointment = (newAppointment) => {
        const newId = appointments.length + 1;
        setAppointments([
            ...appointments,
            {
                id: newId,
                doctor_name: 'Dr. Jane Smith', // Adjust to match your data structure
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [{ author: 'Patient', text: newAppointment.note }],
                isDoctor: false,
            },
        ]);
    };

    return (
        <div className="App">
            <h1>Hospital Management System</h1>

            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />

            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
