import React, { useEffect, useState } from 'react';
import AppointmentCard from './AppointmentCard';

const PatientDashboard = () => {
    const [appointments, setAppointments] = useState([]);
    const [newAppointment, setNewAppointment] = useState({ patientName: '', date: '', note: '' });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate fetching existing appointments
        setTimeout(() => {
            const fetchedAppointments = [
                { id: 1, patientName: 'John Doe', date: '2024-10-15', status: 'Scheduled', note: '' },
                { id: 2, patientName: 'Jane Smith', date: '2024-10-16', status: 'Scheduled', note: '' },
            ];
            setAppointments(fetchedAppointments);
            setLoading(false);
        }, 1000); // Simulate network delay
    }, []);

    const handleAppointmentChange = (e) => {
        const { name, value } = e.target;
        setNewAppointment({ ...newAppointment, [name]: value });
    };

    const bookAppointment = () => {
        if (newAppointment.patientName && newAppointment.date) {
            const updatedAppointments = [...appointments, { id: appointments.length + 1, ...newAppointment, status: 'Scheduled' }];
            setAppointments(updatedAppointments);
            setNewAppointment({ patientName: '', date: '', note: '' }); // Reset form
        }
    };

    if (loading) {
        return <div>Loading appointments...</div>;
    }

    return (
        <div className="patient-dashboard">
            <h1>Patient Dashboard</h1>
            <h2>Current Appointments</h2>
            <div className="appointments">
                {appointments.map((appointment) => (
                    <AppointmentCard
                        key={appointment.id}
                        appointment={appointment}
                    />
                ))}
            </div>
            <h2>Book New Appointment</h2>
            <div className="booking-form">
                <input
                    type="text"
                    name="patientName"
                    placeholder="Your Name"
                    value={newAppointment.patientName}
                    onChange={handleAppointmentChange}
                />
                <input
                    type="date"
                    name="date"
                    value={newAppointment.date}
                    onChange={handleAppointmentChange}
                />
                <input
                    type="text"
                    name="note"
                    placeholder="Additional Notes"
                    value={newAppointment.note}
                    onChange={handleAppointmentChange}
                />
                <button onClick={bookAppointment}>Book Appointment</button>
            </div>
        </div>
    );
};

export default PatientDashboard;
