// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';
import './styles/App.css'; // Ensure this path is correct

const App = () => {
    const [appointments, setAppointments] = useState([]);

    const fetchAppointments = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/appointments');
            setAppointments(response.data);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    useEffect(() => {
        fetchAppointments();
    }, []);

    const handleCancelAppointment = (id) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
            )
        );
    };

    const handleSubmitNote = (id, newNote) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id
                    ? { ...appointment, notes: [...appointment.notes, newNote] }
                    : appointment
            )
        );
    };

    const handleBookAppointment = async (newAppointment) => {
        try {
            const response = await axios.post('http://127.0.0.1:5000/appointments', newAppointment);
            setAppointments((prevAppointments) => [...prevAppointments, response.data]);
        } catch (error) {
            console.error('Error booking appointment:', error);
        }
    };

    return (
        <div className="App">
            <h1>Hospital Management System</h1>
            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />
            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
