// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/App.css';  

const Login = () => {
    const [userType, setUserType] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isSignUp, setIsSignUp] = useState(false); // For Patient signup/login
    const navigate = useNavigate();

    const handleUserTypeChange = (e) => {
        setUserType(e.target.value);
    };

    const handleSignUpToggle = () => {
        setIsSignUp(!isSignUp);
    };

    const handleLogin = async () => {
        try {
            const url = userType === 'Doctor'
                ? 'http://127.0.0.1:5000/doctor/login'
                : isSignUp
                ? 'http://127.0.0.1:5000/patient/signup'
                : 'http://127.0.0.1:5000/patient/login';

            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) {
                throw new Error('Failed to authenticate');
            }

            // Redirect based on user type
            if (userType === 'Doctor') {
                navigate('/doctor-dashboard');
            } else {
                navigate('/patient-dashboard');
            }
        } catch (error) {
            console.error('Login error:', error);
        }
    };

    return (
        <div className="login-container">
            <h1>Login Page</h1>
            <div className="login-options">
                <label>
                    <input
                        type="radio"
                        value="Doctor"
                        checked={userType === 'Doctor'}
                        onChange={handleUserTypeChange}
                    />
                    Doctor Login
                </label>
                <label>
                    <input
                        type="radio"
                        value="Patient"
                        checked={userType === 'Patient'}
                        onChange={handleUserTypeChange}
                    />
                    Patient Login/Signup
                </label>
            </div>

            {userType && (
                <div className="login-form">
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />

                    {userType === 'Patient' && (
                        <div className="signup-toggle">
                            <label>
                                <input
                                    type="checkbox"
                                    checked={isSignUp}
                                    onChange={handleSignUpToggle}
                                />
                                Sign Up
                            </label>
                        </div>
                    )}

                    <button className="login-button" onClick={handleLogin}>
                        {isSignUp ? 'Sign Up' : 'Login'}
                    </button>
                </div>
            )}
        </div>
    );
};

export default Login;
