import React, { useState } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
  const [note, setNote] = useState('');
  const [notes, setNotes] = useState(appointment.notes || []);

  const handleNoteChange = (e) => {
    setNote(e.target.value);
  };

  const handleNoteSubmit = () => {
    if (note.trim() === '') return;
    const newNote = {
      text: note,
      author: appointment.isDoctor ? 'Doctor' : 'Patient',
    };
    setNotes([...notes, newNote]);
    setNote('');
    onSubmitNote(appointment.id, newNote);
  };

  return (
    <div className="appointment-card">
      <h3>Appointment with: {appointment.doctorName}</h3>
      <p>Date: {appointment.date}</p>
      <p>Status: {appointment.status}</p>

      <div className="notes-section">
        {notes.length > 0 && (
          <div>
            <h4>Notes:</h4>
            {notes.map((note, index) => (
              <p key={index}>
                <strong>{note.author} Note:</strong> {note.text}
              </p>
            ))}
          </div>
        )}
      </div>

      <input
        type="text"
        placeholder="Add a note"
        value={note}
        onChange={handleNoteChange}
      />
      <div className="button-group">
        <button className="submit-note" onClick={handleNoteSubmit}>
          Submit Note
        </button>
        <button className="cancel-appointment" onClick={() => onCancel(appointment.id)}>
          Cancel Appointment
        </button>
      </div>
    </div>
  );
};

export default AppointmentCard;
