import React, { useState } from 'react';

const PatientDashboard = () => {
    const [appointments, setAppointments] = useState([
        { id: 1, doctorName: 'Dr. Smith', status: 'Scheduled', note: '' },
    ]);

    const handleCancel = (id) => {
        setAppointments(prevAppointments =>
            prevAppointments.map(appt => 
                appt.id === id ? { ...appt, status: 'Cancelled' } : appt
            )
        );
    };

    const handleNoteChange = (id, newNote) => {
        setAppointments(prevAppointments =>
            prevAppointments.map(appt => 
                appt.id === id ? { ...appt, note: newNote } : appt
            )
        );
    };

    const handleBookAppointment = () => {
        // Add logic to book new appointment here
        alert("Appointment booked!");
    };

    return (
        <div>
            <h1>Patient Dashboard</h1>
            {appointments.map((appointment) => (
                <div key={appointment.id}>
                    <h3>{appointment.doctorName}</h3>
                    <p>Status: {appointment.status}</p>
                    <p>Note: {appointment.note}</p>
                    <button onClick={() => handleCancel(appointment.id)}>Cancel Appointment</button>
                </div>
            ))}
            <div>
                <h3>Book New Appointment</h3>
                <input type="text" placeholder="Note for doctor" onChange={(e) => handleNoteChange(0, e.target.value)} />
                <button onClick={handleBookAppointment}>Book Appointment</button>
            </div>
        </div>
    );
};

export default PatientDashboard;
