// src/components/AppointmentCard.js
import React, { useState } from 'react';
import '../AppointmentCard.css'; // Ensure this path is correct

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');

    const handleNoteSubmit = () => {
        onSubmitNote(appointment.id, { author: 'Doctor', text: note });
        setNote('');
    };

    return (
        <div className="appointment-card">
            <h3>{appointment.doctorName} - {appointment.date}</h3>
            <p>Status: {appointment.status}</p>
            <div className="notes-section">
                <h4>Notes:</h4>
                <ul>
                    {appointment.notes.map((note, index) => (
                        <li key={index} className={note.author === 'Doctor' ? 'doctor-note' : 'patient-note'}>
                            {note.author}: {note.text}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="button-group">
                <button className="cancel-appointment" onClick={() => onCancel(appointment.id)}>Cancel Appointment</button>
                <input
                    type="text"
                    value={note}
                    onChange={(e) => setNote(e.target.value)}
                    placeholder="Add a note"
                />
                <button className="submit-note" onClick={handleNoteSubmit}>Submit Note</button>
            </div>
        </div>
    );
};

export default AppointmentCard;
