import React, { useState } from 'react';
import DoctorDashboard from './components/DoctorDashboard';
import PatientDashboard from './components/PatientDashboard';

const App = () => {
    const [appointments, setAppointments] = useState([]);

    const handleCancelAppointment = (id) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id ? { ...appointment, status: 'Cancelled' } : appointment
            )
        );
    };

    const handleSubmitNote = (id, newNote) => {
        setAppointments((prevAppointments) =>
            prevAppointments.map((appointment) =>
                appointment.id === id
                    ? { ...appointment, notes: [...appointment.notes, newNote] }
                    : appointment
            )
        );
    };

    const handleBookAppointment = (newAppointment) => {
        const newId = appointments.length + 1;
        setAppointments((prevAppointments) => [
            ...prevAppointments,
            {
                id: newId,
                doctorName: 'Dr. Jane Smith', // You can modify this to fetch from the backend in the future
                date: newAppointment.date,
                status: 'Scheduled',
                notes: [{ author: 'Patient', text: newAppointment.note }],
                isDoctor: false,
            },
        ]);
    };

    return (
        <div className="App">
            <h1>Hospital Management System</h1>

            <DoctorDashboard
                appointments={appointments.filter((appointment) => appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
            />

            <PatientDashboard
                appointments={appointments.filter((appointment) => !appointment.isDoctor)}
                onCancel={handleCancelAppointment}
                onSubmitNote={handleSubmitNote}
                onBookAppointment={handleBookAppointment}
            />
        </div>
    );
};

export default App;
