import React, { useState, useEffect } from 'react';

const AppointmentCard = ({ appointment, onCancel, onSubmitNote }) => {
    const [note, setNote] = useState('');
    const [notes, setNotes] = useState([]);

    useEffect(() => {
        if (appointment.notes) {
            setNotes(appointment.notes.map(note => ({ text: note.text, author: note.author === 'Doctor' ? 'Doctor' : 'Patient' })));
        }
    }, [appointment.notes]);

    const handleNoteChange = (e) => {
        setNote(e.target.value);
    };

    const handleNoteSubmit = async () => {
        if (note.trim() === "") return; // Prevent empty note submissions

        const newNote = {
            text: note,
            author: appointment.isDoctor ? 'Doctor' : 'Patient',
        };

        await onSubmitNote(appointment.id, newNote); // Pass the note object directly
        setNotes([...notes, newNote]);
        setNote('');
    };

    return (
        <div className="appointment-card">
            <h3>{appointment.doctor_name}</h3>
            <p>Date: {appointment.date}</p>
            <p>Status: {appointment.status}</p>
            <button onClick={() => onCancel(appointment.id)}>Cancel</button>
            <div>
                <h4>Notes</h4>
                {notes.map((note, index) => (
                    <p key={index}><strong>{note.author}:</strong> {note.text}</p>
                ))}
                <textarea
                    value={note}
                    onChange={handleNoteChange}
                    placeholder="Add a note"
                />
                <button onClick={handleNoteSubmit}>Submit Note</button>
            </div>
        </div>
    );
};

export default AppointmentCard;
