import React, { useState } from 'react';
import AppointmentCard from './AppointmentCard';

const PatientDashboard = ({ appointments, onCancel, onSubmitNote, onBookAppointment }) => {
    const [newAppointment, setNewAppointment] = useState({ date: '', note: '', patientName: '' });

    const handleInputChange = (e) => {
        setNewAppointment({ ...newAppointment, [e.target.name]: e.target.value });
    };

    const handleBookAppointment = () => {
        onBookAppointment(newAppointment);
        setNewAppointment({ date: '', note: '', patientName: '' }); // Reset input fields
    };

    return (
        <div className="dashboard">
            <h2>Patient's Appointments</h2>
            {appointments.length > 0 ? (
                appointments.map((appointment) => (
                    <AppointmentCard
                        key={appointment.id}
                        appointment={appointment}
                        onCancel={onCancel}
                        onSubmitNote={onSubmitNote}
                    />
                ))
            ) : (
                <p>No appointments available.</p>
            )}

            <div className="appointment-booking">
                <h3>Book a new appointment</h3>
                <input
                    type="text"
                    name="patientName"
                    placeholder="Patient Name"
                    value={newAppointment.patientName}
                    onChange={handleInputChange}
                />
                <input
                    type="date"
                    name="date"
                    value={newAppointment.date}
                    onChange={handleInputChange}
                />
                <button onClick={handleBookAppointment}>Book Appointment</button>
            </div>
        </div>
    );
};

export default PatientDashboard;
