import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css'; // Ensure CSS is correctly linked

const App = () => {
    const [appointments, setAppointments] = useState([]);
    const [error, setError] = useState(null);

    const fetchAppointments = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/appointments');
            setAppointments(response.data);
        } catch (err) {
            setError('Error fetching appointments: ' + err.message);
            console.error(err);
        }
    };

    const handleAddAppointment = async () => {
        const newAppointment = {
            doctor_name: 'Dr. Jane Doe',
            patient_name: 'John Smith',
            date: '2024-10-20',
            status: 'Scheduled',
            notes: []
        };

        try {
            await axios.post('http://127.0.0.1:5000/appointments', newAppointment);
            fetchAppointments(); // Refresh appointments after adding
        } catch (err) {
            console.error('Error adding appointment: ', err);
        }
    };

    useEffect(() => {
        fetchAppointments();
    }, []);

    return (
        <div className="App">
            <h1>Appointments</h1>
            {error && <p className="error">{error}</p>}
            <button onClick={handleAddAppointment}>Add Appointment</button>
            <ul>
                {appointments.map((appointment) => (
                    <li key={appointment.id}>
                        {appointment.date} - {appointment.patient_name} with {appointment.doctor_name}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default App;
